import React from "react";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import "../Chatbox.css";
import ChatForm from "./ChatForm";
import { reset } from "redux-form";
import { connect } from "react-redux";
import * as Action from "../Actions/index";
import connection from "../Reducer/ConnectHub";

function Chatbox(props) {
    function test(values) {
        console.log(values.Chat, props.username.username);
        connection.invoke(
            "SendChatMessage",
            values.Chat,
            props.username.username
        );
        props.resetChat();
    }
    const chatList = props.chat.map((chat) => {
        if (chat.username === props.username.username) {
            return (
                <div className="flex-column d-flex">
                    <div className="name_display_send d-flex justify-content-end">
                        {chat.username}
                    </div>
                    <div className="d-flex justify-content-end mb-4 ">
                        <div className="msg_cotainer_send">{chat.message}</div>
                    </div>
                </div>
            );
        }
    });
    return (
        <Col className="chat chatboxflow " md={{ span: 2 }}>
            <Card className="card_chatbox">
                <Card.Header className="msg_head">
                    <div className="d-flex justify-content-between bd-highlight">
                        <div className="user_info">
                            <span>Chat</span>
                        </div>
                        <Button className="btn_close">
                            <i
                                className="fa fa-times"
                                aria-hidden="true"
                                color="white"
                            ></i>
                        </Button>
                    </div>
                </Card.Header>
                <Card.Body className=" msg_card_body">
                    <div className="flex-column d-flex">
                        <div className="name_display d-flex justify-content-start">
                            tandat
                        </div>
                        <div className="d-flex justify-content-start mb-4 ">
                            <div className="msg_cotainer">
                                Hi, how are you samim?
                            </div>
                        </div>
                    </div>
                    <div className="flex-column d-flex">
                        <div className="name_display_send d-flex justify-content-end">
                            tandat2
                        </div>
                        <div className="d-flex justify-content-end mb-4 ">
                            <div className="msg_cotainer_send">
                                Hi Khalid i am good tnx how about you?
                            </div>
                        </div>
                    </div>
                </Card.Body>
                <Card.Footer>
                    <ChatForm onSubmit={test}></ChatForm>
                    {/* <div className="input-group">
                        <div className="input-group-append">
                            <span className="input-group-text attach_btn">
                                <i className="fas fa-paperclip"></i>
                            </span>
                        </div>
                        <textarea
                            name=""
                            className="form-control type_msg"
                            placeholder="Type your message..."
                        ></textarea>
                        <div className="input-group-append">
                            <span className="input-group-text send_btn">
                                <FontAwesomeIcon
                                    icon={faArrowRight}
                                    size="1x"
                                />
                            </span>
                        </div>
                    </div> */}
                </Card.Footer>
            </Card>
        </Col>
    );
}
const mapStateToProps = (state) => ({
    username: state.LoginUserName,
    chat: state.Chat,
});

function mapDispatchToProps(dispatch) {
    return {
        resetChat: () => {
            dispatch(reset("Chat"));
        },
    };
}

export default connect(mapStateToProps, mapDispatchToProps)(Chatbox);
