import React, { Component } from "react";
import { Row, Col, Button, Form, FormGroup, Badge } from "react-bootstrap";
import { connect } from "react-redux";
import { Field, reduxForm } from "redux-form";

class SignUp extends Component {
    renderText = ({ input, meta, type, placeholder }) => {
        // console.log(input.value);
        return (
            <Col>
                <Form.Control
                    {...input}
                    placeholder={placeholder}
                ></Form.Control>{" "}
                {meta.error && <Badge variant="primary">{meta.error}</Badge>}
            </Col>
        );
    };

    render() {
        return (
            <Form className="bg-dark " onSubmit={this.props.handleSubmit}>
                <h2 className="text-center">
                    <Badge variant="info">Sign Up</Badge>
                </h2>

                <FormGroup>
                    <h4>
                        <Badge className="text-light bg-dark">User Name:</Badge>
                    </h4>
                    <Field
                        name="Username"
                        component={this.renderText}
                        type="text"
                        placeholder="Enter UserName"
                    />
                </FormGroup>

                <FormGroup className="form-group">
                    <h4>
                        <Badge className="text-light bg-dark">Email:</Badge>
                    </h4>
                    <Field
                        name="email"
                        component={this.renderText}
                        type="text"
                        placeholder="Enter Email"
                    />
                </FormGroup>

                <div className="form-group">
                    <h4>
                        <Badge className="text-light bg-dark">Password:</Badge>
                    </h4>
                    <Field
                        name="password"
                        component={this.renderText}
                        type="text"
                        placeholder="Enter Password"
                    />
                </div>

                <Button type="submit" block variant="info">
                    Sign Up
                </Button>
            </Form>
        );
    }
}
SignUp = reduxForm({
    // a unique name for the form
    form: "Sigup",
    // initialValues: { name: "", level: 0 },
    // validate,
})(SignUp);

const mapStateToProps = (state) => ({});

const mapDispatchToProps = {};

export default connect(mapStateToProps, mapDispatchToProps)(SignUp);
