import React, { Component } from "react";
import Form from "react-bootstrap/Form";
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";
import ButtonGroup from "react-bootstrap/ButtonGroup";
import { connect } from "react-redux";
import * as Action from "../Actions/index";
function AddTaskForm(props) {
    // constructor(props) {
    //     super(props);
    //     // state = {
    //     //     // adding: props.showForm,
    //     //     // taskName: props.name,
    //     //     // taskLevel: props.level,
    //     //     // oldTaskName: props.name,
    //     // };
    //     closeForm = closeForm.bind(this);
    //     levelChange = levelChange.bind(this);
    //     nameChange = nameChange.bind(this);
    //     submitForm = submitForm.bind(this);
    //     saveForm = saveForm.bind(this);
    // }

    function submitForm() {
        props.addTaskAction({
            name: props.newTaskName,
            level: parseInt(props.newTaskLevel),
        });
    }

    function levelChange(e) {
        props.setNewTask({
            name: props.newTaskName,
            level: e.target.value,
        });
    }

    function nameChange(e) {
        props.setNewTask({
            name: e.target.value,
            level: props.newTaskLevel,
        });
    }
    function saveForm() {
        props.updateTask(
            state.oldTaskName,
            state.taskName,
            parseInt(state.taskLevel)
        );
    }
    function addSaveToggle(edit) {
        if (edit) {
            return (
                <Button onClick={saveForm} variant="primary">
                    Save
                </Button>
            );
        } else
            return (
                <Button onClick={submitForm} variant="primary">
                    Add
                </Button>
            );
    }

    function closeForm() {
        props.closeForm();
    }

    return (
        <Col md={{ span: 5, offset: 7 }}>
            <Form>
                <Form.Row>
                    <Col md={8}>
                        <Form.Group>
                            <Form.Label srOnly={true} htmlFor="true">
                                label
                            </Form.Label>
                            <Form.Control
                                value={props.newTaskName}
                                onChange={nameChange}
                                type="text"
                                className="form-control"
                                placeholder="Task Name"
                                ref="task_name"
                            />
                        </Form.Group>
                    </Col>
                    <Col md={4}>
                        <Form.Label srOnly={true} htmlFor="true">
                            label
                        </Form.Label>
                        <Form.Control
                            value={props.newTaskLevel}
                            as="select"
                            name="ds"
                            id="inputDs"
                            onChange={levelChange}
                        >
                            <option value={1}>Low</option>
                            <option value={2}>Medium</option>
                            <option value={3}>High</option>
                        </Form.Control>
                    </Col>
                </Form.Row>
                <Form.Row>
                    <Col md={12}>
                        <ButtonGroup md={12}>
                            {addSaveToggle(props.edit)}
                            {/* <Button
                                    onClick={submitForm}
                                    variant="primary"
                                >
                                    Add
                                </Button> 
                                closeForm*/}

                            <Button
                                onClick={() => {
                                    props.closeFormAction();
                                }}
                            >
                                Cancel
                            </Button>
                        </ButtonGroup>
                    </Col>
                </Form.Row>
            </Form>
        </Col>
    );
}
const mapStateToProps = (state) => ({
    toggleForm: state.ToggleForm,
    toggleEdit: state.ToggleEdit,
    newTaskName: state.NewTask.name,
    newTaskLevel: state.NewTask.level,
});

function mapDispatchToProps(dispatch, props) {
    return {
        addTaskAction: (task) => {
            dispatch(Action.addTask(task));
        },
        closeFormAction: () => {
            dispatch(Action.closeForm());
        },
        setNewTask: (task) => {
            dispatch(Action.setNewTask(task));
        },
    };
}

export default connect(mapStateToProps, mapDispatchToProps)(AddTaskForm);
