import React, { Component } from "react";
import { Field, reduxForm } from "redux-form";
import * as Action from "../Actions/index";
import Badge from "react-bootstrap/Badge";
import Form from "react-bootstrap/Form";
import ButtonGroup from "react-bootstrap/ButtonGroup";
import Button from "react-bootstrap/Button";
import { connect } from "react-redux";

class EditForm extends Component {
    constructor(props) {
        super(props);
        const { handleSubmit, pristine, reset, submitting } = props;
    }

    renderText = ({ input, meta, type, placeholder }) => {
        return <Form.Control {...input}></Form.Control>;
    };

    renderSelect = ({ input, meta, type, placeholder }) => {
        return (
            <Form.Control as="select" custom {...input}>
                <option
                    value={0}
                    style={{ background: "black", color: "white" }}
                >
                    Choose Priority
                </option>
                <option value={1}>Low</option>
                <option value={2}>Medium</option>
                <option value={3}>High</option>
            </Form.Control>
        );
    };
    level(level) {
        switch (level) {
            case 1:
                return <Badge variant="light">Low</Badge>;
            case 2:
                return <Badge variant="info">Medium</Badge>;
            case 3:
                return <Badge variant="danger">High</Badge>;
            default:
                return <Badge variant="primary">No Level</Badge>;
        }
    }
    render() {
        return (
            <tr>
                <td className="text-center">{this.props.index + 1}</td>
                <td colSpan={3} className="td">
                    <div>
                        <Form
                            onSubmit={this.handleSubmit}
                            className="container-form"
                        >
                            {/* <div className="container"> */}
                            <div className="name">
                                <h6>Old Name: {this.props.task.name}</h6>
                                <div>
                                    <Field
                                        className="form-control"
                                        name="name"
                                        component={this.renderText}
                                        // component="input"
                                        // type="text"
                                        placeholder="First Name"
                                    />
                                </div>
                            </div>

                            <div className="level">
                                <h6>
                                    Old Level:{" "}
                                    {this.level(this.props.task.level)}
                                </h6>

                                <div>
                                    <Field
                                        name="level"
                                        // component="select"
                                        component={this.renderSelect}
                                    ></Field>
                                </div>
                            </div>

                            <div className="action">
                                <div className="button-form text-center  ">
                                    <ButtonGroup
                                        aria-label="Basic example"
                                        style={{
                                            width: "100%",
                                            height: "100%",
                                        }}
                                    >
                                        <Button variant="warning" type="submit">
                                            Done
                                        </Button>
                                        <Button
                                            variant="danger"
                                            onClick={() => {
                                                this.props.onClose();
                                            }}
                                        >
                                            Cancel
                                        </Button>
                                        {/* <Button variant="secondary">Right</Button> */}
                                    </ButtonGroup>
                                </div>
                            </div>
                            {/* </div> */}
                        </Form>
                    </div>
                </td>
            </tr>
        );
    }
}
const mapStateToProps = (state) => ({});

const mapDispatchToProps = (dispatch) => {
    return {
        toggleEdit: () => {
            dispatch(Action.toggleEdit());
        },
    };
};

const validate = (values) => {
    const err = {};
    if (!values.name) {
        err.name = "Required";
    }
    if (!values.level) {
        err.name = "Required";
    }
};

EditForm = reduxForm({
    // a unique name for the form
    form: "row",
})(EditForm);

export default connect(mapStateToProps, mapDispatchToProps)(EditForm);
