import * as TYPE from "../Const/Types";

const initialState = false;

export default (state = initialState, action) => {
    switch (action.type) {
        case TYPE.TOGGLE_FORM:
            return !state;
        case TYPE.CLOSE_FORM:
            return false;
        case TYPE.OPEN_FORM:
            return true;

        default:
            return state;
    }
};
