import React, { Component } from "react";
import Card from "react-bootstrap/Card";
import Col from "react-bootstrap/Col";
import Table from "react-bootstrap/Table";
import Badge from "react-bootstrap/Badge";
import ButtonGroup from "react-bootstrap/ButtonGroup";
import Button from "react-bootstrap/Button";

import TaskList from "../Tasks/Task.json";

export default class TaskTable extends Component {
    constructor(props) {
        super(props);

        this.state = { TaskList: TaskList };
        //console.log(this.state.TaskList.Task);
        this.delete = this.delete.bind(this);
    }

    delete(name) {
        const tmpArr = this.state.TaskList.Task.filter((task) => {
            return task.name !== name;
        });
        this.setState = {
            TaskList: tmpArr,
        };
        console.log(this.state.TaskList);
    }

    render() {
        const level = (level) => {
            switch (level) {
                case 1:
                    return <Badge variant="light">Small</Badge>;
                case 2:
                    return <Badge variant="info">Medium</Badge>;
                case 3:
                    return <Badge variant="danger">High</Badge>;

                default:
                    return <Badge variant="primary">No Level</Badge>;
            }
        };

        const taskList = this.state.TaskList.Task.map((task, index) => {
            return (
                <tr key={index}>
                    <td className="text-center">{index + 1}</td>
                    <td>{task.name}</td>
                    <td className="text-center">{level(task.level)}</td>
                    <td className="text-center">
                        <ButtonGroup>
                            <Button variant="warning">Edit</Button>
                            <Button
                                variant="danger"
                                onClick={() => {
                                    return this.delete(task.name);
                                }}
                            >
                                Delete
                            </Button>
                        </ButtonGroup>
                    </td>
                </tr>
            );
        });
        return (
            <Col>
                <Card>
                    <Card.Body>
                        <Card.Header>List Task</Card.Header>
                        <Table striped bordered hover variant="dark">
                            <thead>
                                <tr>
                                    <th
                                        style={{ width: "10%" }}
                                        className="text-center"
                                    >
                                        #
                                    </th>
                                    <th>Task</th>
                                    <th
                                        style={{ width: "20%" }}
                                        className="text-center"
                                    >
                                        Level
                                    </th>
                                    <th
                                        style={{ width: "20%" }}
                                        className="text-center"
                                    >
                                        Action
                                    </th>
                                </tr>
                            </thead>
                            <tbody>{taskList}</tbody>
                        </Table>
                    </Card.Body>
                </Card>
            </Col>
        );
    }
}
