import * as TYPE from "../Const/Types";
export const listAll = () => {
    return {
        type: TYPE.LIST,
    };
};

export const addTask = (task) => {
    return {
        type: TYPE.ADD_TASK,
        task: task,
    };
};

export const deleteTask = (taskName) => {
    return {
        type: TYPE.DELETE_TASK,
        taskName: taskName,
    };
};

export const toggleForm = () => {
    return {
        type: TYPE.TOGGLE_FORM,
    };
};

export const openForm = () => {
    return {
        type: TYPE.OPEN_FORM,
    };
};

export const closeForm = () => {
    return {
        type: TYPE.CLOSE_FORM,
    };
};

export const toggleEdit = () => {
    return {
        type: TYPE.TOGGLE_EDIT,
    };
};

export const editTask = (task) => {
    return {
        type: TYPE.EDIT_TASK,
        task: task,
    };
};

export const newTask = (task) => {
    return {
        type: TYPE.NEW_TASK,
        task: task,
    };
};

export const updateTask = (odlName, newTask) => {
    return {
        type: TYPE.UPDATE_TASK,
        oldName: odlName,
        newTask: newTask,
    };
};

export const search = (search) => {
    return {
        type: TYPE.SEARCH,
        search: search,
    };
};

export const sort = (by, dir) => {
    return {
        type: TYPE.SORT,
        by: by,
        dir: dir,
    };
};

export const resetNewTask = () => {
    return {
        type: TYPE.RESET_NEW_TASK,
    };
};

export const setNewTask = (task) => {
    return {
        type: TYPE.SET_NEW_TASK,
        task: task,
    };
};

export const toggleChart = () => {
    return {
        type: TYPE.TOGGLE_CHART,
    };
};

export const resetChart = () => {
    return {
        type: TYPE.UPDATE_CHART,
    };
};
