import * as TYPE from "../Const/Types";
import TaskList from "../Tasks/Task.json";
import * as URL from "../Const/URL";
import Cookies from "js-cookie";
import { wait } from "@testing-library/react";
// import $ from "jquery";
var local;
local = localStorage.getItem("Tasks");
if (local === "undefined") {
    console.log("resetdata");
    //local = JSON.stringify(TaskList.Task);
    localStorage.setItem("Tasks", JSON.stringify(TaskList.Task));
}
local = localStorage.getItem("Tasks");
function getTask() {
    fetch(URL.URL_API)
        .then((response) => response.json())
        .catch((e) => {
            console.log("Err", e);
        })
        .then((data) => {
            console.log(data);
            Cookies.set("Task", data);
            console.log(Cookies.get("Task"));
        });
}
getTask();
// fetch("http://localhost:3000/api/TodoTasks")
//     .then((response) => response.json())
//     .catch((e) => {
//         console.log("Err", e);
//     })
//     .then((data) => {
//         console.log(data);
//         Cookies.set("Task", data);
//         console.log(Cookies.get("Task"));
//     });

console.log(JSON.parse(Cookies.get("Task")));
const initialState = JSON.parse(Cookies.get("Task"));

function addTask(task) {
    const item = task;

    fetch(URL.URL_API, {
        method: "POST",
        headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
        },
        body: JSON.stringify(item),
    })
        .catch((error) => console.error("Unable to add item.", error))
        .then(() => {
            getTask();
        });
    return [...JSON.parse(Cookies.get("Task"))];
}

function sort(arr, by, dir) {
    switch (by) {
        case "Name":
            switch (dir) {
                case "DESC":
                    return arr.sort((a, b) => {
                        // console.log("DESC", a.name - b.name);
                        if (b.name < a.name) {
                            return -1;
                        }
                        if (b.name > a.name) {
                            return 1;
                        }

                        // name trùng nhau
                        return 0;
                        // return b.name - a.name;
                    });

                default:
                    return arr.sort((a, b) => {
                        // console.log("ASC", a.name - b.name);
                        if (b.name < a.name) {
                            return 1;
                        }
                        if (b.name > a.name) {
                            return -1;
                        }
                        return 0;
                    });
            }

        default:
            switch (dir) {
                case "DESC":
                    return arr.sort((a, b) => {
                        return b.level - a.level;
                    });

                default:
                    return arr.sort((a, b) => {
                        return a.level - b.level;
                    });
            }
    }
}

export default (state = initialState, action) => {
    switch (action.type) {
        case TYPE.LIST:
            // console.log("reducer", action);
            return state;
        case TYPE.ADD_TASK:
            // console.log("reducer", action);
            // addTask(action.task);

            // state.push(action.task);
            console.log("state", JSON.parse(Cookies.get("Task")));
            // console.log("js", JSON.stringify(state));
            // localStorage.setItem("Tasks", JSON.stringify(state));
            JSON.parse(Cookies.get("Task"));
            return addTask(action.task);
        case TYPE.DELETE_TASK:
            // console.log("reducer", action);

            let tmpArr = JSON.parse(localStorage.getItem("Tasks")).filter(
                (task) => {
                    return task.name !== action.taskName;
                }
            );
            let tmpArr2 = state.filter((task) => {
                return task.name !== action.taskName;
            });
            // console.log("state", tmpArr);
            // console.log("js", JSON.stringify(tmpArr));
            localStorage.setItem("Tasks", JSON.stringify(tmpArr));

            return [...tmpArr2];
        case TYPE.UPDATE_TASK:
            console.log("Update", action);
            let updateArr = state.map((task) => {
                if (task.name === action.oldName) {
                    return {
                        name: action.newTask.name,
                        level: action.newTask.level,
                    };
                } else return task;
            });
            // console.log("state", updateArr);
            // console.log("js", JSON.stringify(updateArr));
            localStorage.setItem("Tasks", JSON.stringify(updateArr));
            return [...updateArr];
        case TYPE.SEARCH:
            // console.log("SEARCH", action);
            // JSON.parse(localStorage.getItem("Tasks"))
            const taskListSearched = JSON.parse(
                localStorage.getItem("Tasks")
            ).filter((task) => {
                return task.name
                    .toLowerCase()
                    .includes(action.search.toLowerCase());
            });
            // console.log("state", state);
            // console.log("js", JSON.stringify(taskListSearched));
            // localStorage.setItem("Tasks", JSON.stringify(updateArr));
            return [...taskListSearched];
        case TYPE.SORT:
            // console.log("Sort", action);
            // console.log("state", state);

            // JSON.parse(localStorage.getItem("Tasks"))
            const taskListSorted = sort(state, action.by, action.dir);
            // console.log("state", taskListSorted);
            // console.log("js", JSON.stringify(taskListSearched));
            // localStorage.setItem("Tasks", JSON.stringify(updateArr));
            return [...taskListSorted];
        default:
            return state;
    }
    return state;
};
