import * as TYPE from "../Const/Types";
export const listAll = () => {
    return {
        type: TYPE.LIST,
    };
};

export const addTask = (task) => {
    return {
        type: TYPE.ADD_TASK,
        task: task,
    };
};

export const deleteTask = (taskName) => {
    return {
        type: TYPE.DELETE_TASK,
        taskName: taskName,
    };
};

export const toggleForm = (taskName) => {
    return {
        type: TYPE.TOGGLE_FORM,
    };
};
