import React from "react";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Card from "react-bootstrap/Card";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import "../Chatbox.css";
import InputGroup from "react-bootstrap/InputGroup";
import ChatForm from "./ChatForm";
import { reset } from "redux-form";
import { connect } from "react-redux";
import * as Action from "../Actions/index";
import connection from "../Reducer/ConnectHub";

function Chatbox(props) {
    function test(values) {
        console.log(values.Chat, props.username.username);
        connection.invoke(
            "SendChatMessage",
            values.Chat,
            props.username.username
        );
        props.resetChat();
    }
    const chatList = props.chat.map((chat, index) => {
        if (chat.username === props.username.username) {
            return (
                <div className="flex-column d-flex mb-2" key={index}>
                    <div className="name_display_send d-flex justify-content-end">
                        {chat.username}
                    </div>
                    <div className="d-flex justify-content-end">
                        <div className="msg_cotainer_send">{chat.message}</div>
                    </div>
                </div>
            );
        } else
            return (
                <div className="flex-column d-flex mb-2" key={index}>
                    <div className="name_display d-flex justify-content-start">
                        {chat.username}
                    </div>
                    <div className="d-flex justify-content-start  ">
                        <div className="msg_cotainer">{chat.message}</div>
                    </div>
                </div>
            );
    });
    return (
        <Col className="chat chatboxflow " md={{ span: 3 }}>
            <Card className="card_chatbox">
                <Card.Header className="msg_head">
                    <div className="d-flex justify-content-between bd-highlight">
                        <Form>
                            <Form.Row>
                                <Form.Control as="select">
                                    <option>1</option>
                                    <option>2</option>
                                    <option>3</option>
                                    <option>4</option>
                                    <option>5</option>
                                </Form.Control>
                            </Form.Row>
                        </Form>
                    </div>
                </Card.Header>
                <Card.Body className=" msg_card_body">{chatList}</Card.Body>
                <Card.Footer>
                    <ChatForm onSubmit={test}></ChatForm>
                </Card.Footer>
            </Card>
        </Col>
    );
}
const mapStateToProps = (state) => ({
    username: state.LoginUserName,
    chat: state.Chat,
});

function mapDispatchToProps(dispatch) {
    return {
        resetChat: () => {
            dispatch(reset("Chat"));
        },
    };
}

export default connect(mapStateToProps, mapDispatchToProps)(Chatbox);
