import React, { Component } from "react";
import ButtonGroup from "react-bootstrap/ButtonGroup";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Badge from "react-bootstrap/Badge";
import { connect } from "react-redux";
import * as Action from "../Actions/index";
// import Edit from "./Edit";
class TaskRow extends Component {
    constructor(props) {
        super(props);
        this.nameChange = this.nameChange.bind(this);
        this.levelChange = this.levelChange.bind(this);
    }

    update(oldName, newName, newLevel) {
        console.log(oldName, newName, newLevel);
        // const index = this.state.Task.findIndex((task) => task.name == oldName);
        this.props.updateTask(oldName, { name: newName, level: newLevel });
    }

    handleSave() {
        this.props.toggleEdit();
        console.log(
            this.props.editName,
            this.props.newTaskName,
            parseInt(this.props.newTaskLevel)
        );

        this.update(
            this.props.editName,
            this.props.newTaskName,
            parseInt(this.props.newTaskLevel)
        );
    }

    toggleEditOrSave(task) {
        if (this.props.isEdit) {
            return (
                <Button
                    variant="warning"
                    onClick={() => this.handleSave(task.name, task.level)}
                >
                    Save
                </Button>
            );
        } else {
            return (
                <Button
                    variant="warning"
                    onClick={() => this.handleEdit(task.name, task.level)}
                >
                    Edit
                </Button>
            );
        }
    }

    handleEdit(name, level) {
        this.props.toggleEdit();
        this.props.newTask({ name: name, level: level });
        this.props.editTask({ name: name, level: level });
    }

    level(level) {
        switch (level) {
            case 1:
                return <Badge variant="light">Low</Badge>;
            case 2:
                return <Badge variant="info">Medium</Badge>;
            case 3:
                return <Badge variant="danger">High</Badge>;
            default:
                return <Badge variant="primary">No Level</Badge>;
        }
    }

    nameChange(e) {
        // this.setState({ newName: e.target.value });
        this.props.newTask({
            name: e.target.value,
            level: this.props.newTaskLevel,
        });
    }

    isEditName(task) {
        if (task.name !== this.props.editName || this.props.isEdit === false) {
            console.log(
                "nor ",
                task.name,
                this.props.editName,
                this.props.isEdit
            );

            return <td>{task.name}</td>;
        } else if (this.props.isEdit === true) {
            // console.log(
            //     "tex ",
            //     task.name,
            //     this.props.editName,
            //     this.props.isEdit
            // );
            return (
                // <Edit newName={this.state.newName}></Edit>
                <td>
                    <Form.Control
                        type="text"
                        value={this.props.newTaskName}
                        onChange={this.nameChange}
                    ></Form.Control>
                </td>
            );
        }
    }

    // delete(name) {
    //     // console.log("delete");
    //     const tmpArr = this.state.Task.filter((task) => {
    //         return task.name !== name;
    //     });
    //     this.setState({
    //         Task: tmpArr,
    //     });
    // }

    isEditLevel(task) {
        if (task.name !== this.props.editName || this.props.isEdit === false) {
            return <td className="text-center">{this.level(task.level)}</td>;
        } else if (this.props.isEdit === true) {
            // console.log("select ", task.name);
            return (
                <td className="text-center">
                    <Form.Control
                        value={this.props.newTaskLevel}
                        as="select"
                        name="ds"
                        id="inputDs"
                        onChange={this.levelChange}
                    >
                        <option value={1}>Low</option>
                        <option value={2}>Medium</option>
                        <option value={3}>High</option>
                    </Form.Control>
                </td>
            );
        }
    }

    levelChange(e) {
        // this.setState({
        //     newLevel: e.target.value,
        // });
        console.log(e.target.value);
        this.props.newTask({
            name: this.props.newTaskName,
            level: e.target.value,
        });
    }

    render() {
        console.log("row-render");
        // console.log(
        //     "this.props.editTaskTarget",
        //     this.props.editTaskTarget ? this.props.editTaskTarget : null
        // );
        return (
            // <tr key={index}>
            <tr>
                <td className="text-center">{this.props.index + 1}</td>
                {this.isEditName(this.props.task)}
                {this.isEditLevel(this.props.task)}
                {/* <td>{task.name}</td> */}
                {/* <td className="text-center">{this.level(task.level)}</td> */}
                <td className="text-center">
                    <ButtonGroup>
                        {this.toggleEditOrSave(this.props.task)}
                        {/* <Button
                    variant="warning"
                    onClick={() =>
                        this.handleEdit(task.name, task.level)
                    }
                >
                    Edit
                </Button> */}
                        <Button
                            variant="danger"
                            onClick={() => {
                                // return this.delete(this.state.task.name);
                                return this.props.deleteTask(
                                    this.props.task.name
                                );
                            }}
                        >
                            Delete
                        </Button>
                    </ButtonGroup>
                </td>
            </tr>
        );
    }
}

const mapStateToProps = (state) => ({
    isEdit: state.ToggleEdit,
    editName: state.EditTask.name,
    // editTaskTarget: state.EditTask,
    newTaskName: state.NewTask.name,
    newTaskLevel: state.NewTask.level,
});

function mapDispatchToProps(dispatch) {
    return {
        deleteTask: (taskName) => {
            dispatch(Action.deleteTask(taskName));
        },
        toggleEdit: () => {
            dispatch(Action.toggleEdit());
        },
        editTask: (task) => {
            dispatch(Action.editTask(task));
        },
        newTask: (task) => {
            dispatch(Action.newTask(task));
        },
        updateTask: (oldName, newTask) => {
            dispatch(Action.updateTask(oldName, newTask));
        },
    };
}

export default connect(mapStateToProps, mapDispatchToProps)(TaskRow);
