import React, { Component } from "react";
import { Row, Col, Button, Form, FormGroup, Badge } from "react-bootstrap";
import { connect } from "react-redux";
import { Field, reduxForm } from "redux-form";

class FormPage extends Component {
    renderText = ({ input, meta, type, placeholder }) => {
        // console.log(input.value);
        return (
            <div>
                <Form.Control
                    className="text-light bg-dark"
                    {...input}
                    placeholder={placeholder}
                ></Form.Control>{" "}
                {meta.error && <Badge variant="primary">{meta.error}</Badge>}
            </div>
        );
    };
    render() {
        return (
            <Form className="bg-dark">
                <h3>Sign In</h3>

                <FormGroup onSubmit={this.props.handleSubmit}>
                    <h4>
                        <Badge className="text-light bg-dark">UserName</Badge>
                    </h4>
                    <Field
                        name="Username"
                        component={this.renderText}
                        type="text"
                        placeholder="Enter UserName"
                    />
                </FormGroup>

                <FormGroup>
                    <h4>
                        {" "}
                        <Badge className="text-light bg-dark">Password</Badge>
                    </h4>
                    <Field
                        name="Password"
                        component={this.renderText}
                        type="password"
                        placeholder="Enter Password"
                    />
                </FormGroup>

                <Button type="submit" block variant="outline-light">
                    Submit
                </Button>
            </Form>
        );
    }
}
FormPage = reduxForm({
    // a unique name for the form
    form: "Sigup",
    // initialValues: { name: "", level: 0 },
    // validate,
})(FormPage);

const mapStateToProps = (state) => ({});

const mapDispatchToProps = {};

export default connect(mapStateToProps, mapDispatchToProps)(FormPage);
