import React, { Component } from "react";
import { Row, Col, Button, Form, FormGroup, Badge } from "react-bootstrap";
import { connect } from "react-redux";
import { Field, reduxForm } from "redux-form";

class FormPage extends Component {
    renderText = ({ input, meta, type, placeholder }) => {
        // console.log(input.value);
        return (
            <div>
                <Form.Control
                    {...input}
                    placeholder={placeholder}
                ></Form.Control>{" "}
                {meta.error && <Badge variant="primary">{meta.error}</Badge>}
            </div>
        );
    };
    render() {
        return (
            <Form>
                <h3>Sign In</h3>

                <FormGroup>
                    <label>UserName</label>
                    <Field
                        component={this.renderText}
                        type="text"
                        placeholder="Enter UserName"
                    />
                </FormGroup>

                <FormGroup>
                    <label>Password</label>
                    <Form.Control
                        type="password"
                        placeholder="Enter password"
                    />
                </FormGroup>

                <div className="form-group">
                    <div className="custom-control custom-checkbox">
                        <input
                            type="checkbox"
                            className="custom-control-input"
                            id="customCheck1"
                        />
                        <label
                            className="custom-control-label"
                            htmlFor="customCheck1"
                        >
                            Remember me
                        </label>
                    </div>
                </div>

                <button type="submit" className="btn btn-primary btn-block">
                    Submit
                </button>
            </Form>
        );
    }
}
FormPage = reduxForm({
    // a unique name for the form
    // initialValues: { name: "", level: 0 },
    // validate,
})(FormPage);

const mapStateToProps = (state) => ({});

const mapDispatchToProps = {};

export default connect(mapStateToProps, mapDispatchToProps)(FormPage);
