import React, { useState } from "react";
import { Field, reduxForm } from "redux-form";
import Form from "react-bootstrap/Form";
import * as Action from "../Actions/index";
import { connect } from "react-redux";

function EditFields(props) {
    const [nameChange, setNameChange] = useState(props.nameChange);

    const renderField = ({ input, label, type, meta: { touched, error } }) => (
        <Form>
            <label>{nameChange}</label>
            <div>
                <input
                    {...input}
                    placeholder={label}
                    type={type}
                    className="form-control"
                    value={nameChange}
                />
            </div>
            {touched && error && <span className="text-danger">{error}</span>}
        </Form>
    );
    console.log(nameChange);
    return (
        <Field
            // previousValue={nameChange}
            name="row"
            component={renderField}
            type="text"
        />
    );
}
EditFields = reduxForm({
    // a unique name for the form
    form: "contact",
    enableReinitialize: true,
})(EditFields);
const mapStateToProps = (state) => ({
    initialValues: state.EditTask.name,
});

const mapDispatchToProps = (dispatch) => {
    return {
        updateChart: () => {
            dispatch(Action.resetChart());
        },
    };
};

export default connect()(EditFields);
