import React, { useState } from "react";
import { Field, reduxForm } from "redux-form";
import Form from "react-bootstrap/Form";
import Badge from "react-bootstrap/Badge";
import * as Action from "../Actions/index";
import { connect } from "react-redux";

function EditFields(props) {
    const [nameChange, setNameChange] = useState(props.nameChange);

    const renderField = ({ input, label, type, meta: { touched, error } }) => {
        console.log(input);
        return (
            <Form>
                <h5>
                    <Badge pill variant="primary">
                        {"OldName: " + nameChange}
                    </Badge>
                </h5>
                <div>
                    <input
                        // {...input}
                        placeholder={label}
                        type={type}
                        className="form-control"
                        value={input.value}
                    />
                </div>
                {/* {touched && error && <span className="text-danger">{error}</span>} */}
            </Form>
        );
    };
    console.log(nameChange);
    return (
        <Field
            // previousValue={nameChange}
            name="row"
            component={renderField}
            type="text"
        />
    );
}
EditFields = reduxForm({
    // a unique name for the form
    form: "contact",
    enableReinitialize: true,
})(EditFields);
const mapStateToProps = (state) => ({
    initialValues: state.EditTask.name,
});

const mapDispatchToProps = (dispatch) => {
    return {
        updateChart: () => {
            dispatch(Action.resetChart());
        },
    };
};

export default connect()(EditFields);
