import React, { useState } from "react";
import { Field, reduxForm } from "redux-form";
import Form from "react-bootstrap/Form";
import * as Action from "../Actions/index";
import { connect } from "react-redux";

function EditFields(props) {
    const [nameChange, setNameChange] = useState(props.nameChange);

    console.log(nameChange);
    return (
        <form>
            <Field
                // previousValue={nameChange}
                name="row"
                value={props.nameChange}
                className="form-control"
                component="input"
                type="text"
            />
        </form>
    );
}
EditFields = reduxForm({
    // a unique name for the form
    form: "contact",
})(EditFields);
const mapStateToProps = (state) => ({
    initialValues: state.EditTask.name,
});

const mapDispatchToProps = (dispatch) => {
    return {
        updateChart: () => {
            dispatch(Action.resetChart());
        },
    };
};

export default connect()(EditFields);
