import React, { Component } from "react";
import { Row, Col, Button, Form, FormGroup, Badge } from "react-bootstrap";
import { connect } from "react-redux";
import { Field, reduxForm } from "redux-form";

class SignUp extends Component {
    renderText = ({ input, meta, type, placeholder }) => {
        // console.log(input.value);
        return (
            <div>
                <Form.Control
                    {...input}
                    placeholder={placeholder}
                ></Form.Control>{" "}
                {meta.error && <Badge variant="primary">{meta.error}</Badge>}
            </div>
        );
    };

    render() {
        return (
            <Form>
                <h2 className="text-center">
                    <Badge variant="info">Sign Up</Badge>
                </h2>

                <FormGroup>
                    <h4>
                        <Badge className="text-light bg-dark">UserName</Badge>
                    </h4>
                    <Field
                        name="Username"
                        component={this.renderText}
                        type="text"
                        placeholder="Enter UserName"
                    />
                </FormGroup>

                <div className="form-group">
                    <label>Last name</label>
                    <Field
                        name="Username"
                        component={this.renderText}
                        type="text"
                        placeholder="Enter UserName"
                    />
                </div>

                <div className="form-group">
                    <label>Email address</label>
                    <Field
                        name="Username"
                        component={this.renderText}
                        type="text"
                        placeholder="Enter UserName"
                    />
                </div>

                <div className="form-group">
                    <label>Password</label>
                    <Field
                        name="Username"
                        component={this.renderText}
                        type="text"
                        placeholder="Enter UserName"
                    />
                </div>

                <button type="submit" className="btn btn-primary btn-block">
                    Sign Up
                </button>
                <p className="forgot-password text-right">
                    Already registered <a href="#">sign in?</a>
                </p>
            </Form>
        );
    }
}
SignUp = reduxForm({
    // a unique name for the form
    form: "Sigup",
    // initialValues: { name: "", level: 0 },
    // validate,
})(SignUp);

const mapStateToProps = (state) => ({});

const mapDispatchToProps = {};

export default connect(mapStateToProps, mapDispatchToProps)(SignUp);
