import React, { useState, useEffect } from "react";
import ButtonGroup from "react-bootstrap/ButtonGroup";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Badge from "react-bootstrap/Badge";
import { connect } from "react-redux";
import * as Action from "../Actions/index";
// import React, { useState, useEffect } from "react";
// import Edit from "./Edit";
function TaskRow(props) {
    // constructor(props) {
    //     super(props);
    //     this.nameChange = this.nameChange.bind(this);
    //     this.levelChange = this.levelChange.bind(this);
    // }

    const [nameChange, setNameChange] = useState(this.state.newTaskName);
    const [levelChange, setLevelChange] = useState(this.state.newTaskLevel);
    const [editName, setEditName] = useState(this.state.editName);
    const [isEdit, setIsEdit] = useState(false);

    function update(oldName, newName, newLevel) {
        console.log(oldName, newName, newLevel);
        // const index = this.state.Task.findIndex((task) => task.name == oldName);
        props.updateTask(oldName, { name: newName, level: newLevel });
    }

    function handleSave() {
        props.toggleEdit();
        console.log(
            props.editName,
            props.newTaskName,
            parseInt(props.newTaskLevel)
        );

        update(props.editName, props.newTaskName, parseInt(props.newTaskLevel));
    }

    function toggleEditOrSave(task) {
        if (isEdit) {
            return (
                <Button
                    variant="warning"
                    onClick={() => handleSave(task.name, task.level)}
                >
                    Save
                </Button>
            );
        } else {
            return (
                <Button
                    variant="warning"
                    onClick={() => handleEdit(task.name, task.level)}
                >
                    Edit
                </Button>
            );
        }
    }

    function shouldComponentUpdate(nextProps, nextState) {}

    function handleEdit(name, level) {
        setIsEdit(true);
        // props.toggleEdit();
        props.newTask({ name: name, level: level });
        props.editTask({ name: name, level: level });
    }

    function level(level) {
        switch (level) {
            case 1:
                return <Badge variant="light">Low</Badge>;
            case 2:
                return <Badge variant="info">Medium</Badge>;
            case 3:
                return <Badge variant="danger">High</Badge>;
            default:
                return <Badge variant="primary">No Level</Badge>;
        }
    }

    // function nameChange(e) {
    //     // this.setState({ newName: e.target.value });
    //     this.props.newTask({
    //         name: e.target.value,
    //         level: this.props.newTaskLevel,
    //     });
    // }

    function isEditName(task) {
        if (task.name !== props.editName || isEdit === false) {
            // console.log(
            //     "nor ",
            //     task.name,
            //     this.props.editName,
            //     this.props.isEdit
            // );

            return <td>{task.name}</td>;
        } else if (props.isEdit === true) {
            // console.log(
            //     "tex ",
            //     task.name,
            //     this.props.editName,
            //     this.props.isEdit
            // );
            return (
                // <Edit newName={this.state.newName}></Edit>
                <td>
                    <Form.Control
                        type="text"
                        value={nameChange}
                        onChange={(e) => setNameChange(e.target.value)}
                    ></Form.Control>
                </td>
            );
        }
    }

    function isEditLevel(task) {
        if (task.name !== props.editName || props.isEdit === false) {
            return <td className="text-center">{level(task.level)}</td>;
        } else if (props.isEdit === true) {
            // console.log("select ", task.name);
            return (
                <td className="text-center">
                    <Form.Control
                        value={levelChange}
                        as="select"
                        name="ds"
                        id="inputDs"
                        onChange={(e) => setLevelChange(e.target.value)}
                    >
                        <option value={1}>Low</option>
                        <option value={2}>Medium</option>
                        <option value={3}>High</option>
                    </Form.Control>
                </td>
            );
        }
    }

    // function levelChange(e) {
    //     // this.setState({
    //     //     newLevel: e.target.value,
    //     // });
    //     console.log(e.target.value);
    //     this.props.newTask({
    //         name: this.props.newTaskName,
    //         level: e.target.value,
    //     });
    // }

    console.log("row-render");
    // console.log(
    //     "this.props.editTaskTarget",
    //     this.props.editTaskTarget ? this.props.editTaskTarget : null
    // );
    return (
        // <tr key={index}>
        <tr>
            <td className="text-center">{props.index + 1}</td>
            {isEditName(props.task)}
            {isEditLevel(props.task)}
            {/* <td>{task.name}</td> */}
            {/* <td className="text-center">{this.level(task.level)}</td> */}
            <td className="text-center">
                <ButtonGroup>
                    {toggleEditOrSave(props.task)}
                    {/* <Button
                    variant="warning"
                    onClick={() =>
                        this.handleEdit(task.name, task.level)
                    }
                >
                    Edit
                </Button> */}
                    <Button
                        variant="danger"
                        onClick={() => {
                            // return this.delete(this.state.task.name);
                            return props.deleteTask(props.task.name);
                        }}
                    >
                        Delete
                    </Button>
                </ButtonGroup>
            </td>
        </tr>
    );
}

const mapStateToProps = (state) => ({
    // isEdit: state.ToggleEdit,
    editName: state.EditTask.name,
    // editTaskTarget: state.EditTask,
    newTaskName: state.NewTask.name,
    newTaskLevel: state.NewTask.level,
});

function mapDispatchToProps(dispatch) {
    return {
        deleteTask: (taskName) => {
            dispatch(Action.deleteTask(taskName));
        },
        toggleEdit: () => {
            dispatch(Action.toggleEdit());
        },
        editTask: (task) => {
            dispatch(Action.editTask(task));
        },
        newTask: (task) => {
            dispatch(Action.newTask(task));
        },
        updateTask: (oldName, newTask) => {
            dispatch(Action.updateTask(oldName, newTask));
        },
    };
}

export default connect(mapStateToProps, mapDispatchToProps)(TaskRow);
