import React, { Component } from "react";
import ButtonGroup from "react-bootstrap/ButtonGroup";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Edit from "./Edit";
export default class TaskRow extends Component {
    constructor(props) {
        super(props);
        this.state = {
            index: this.props.index,
            task: this.props.task,
            newLevel: 1,
        };
    }

    toggleEditOrSave(task) {
        if (this.state.isEdit) {
            return (
                <Button
                    variant="warning"
                    onClick={() => this.handleSave(task.name, task.level)}
                >
                    Save
                </Button>
            );
        } else {
            return (
                <Button
                    variant="warning"
                    onClick={() => this.handleEdit(task.name, task.level)}
                >
                    Edit
                </Button>
            );
        }
    }

    isEditName(task) {
        if (task.name !== this.state.editName || this.state.isEdit === false) {
            // console.log("nor ", task.name);
            return <td>{task.name}</td>;
        } else if (this.state.isEdit === true) {
            // console.log("text ", task.name);
            return (
                <Edit newName={this.state.newName}></Edit>
                // <td>
                //     <Form.Control
                //         type="text"
                //         value={this.state.newName}
                //         onChange={this.nameChange}
                //     ></Form.Control>
                // </td>
            );
        }
    }

    delete(name) {
        // console.log("delete");
        const tmpArr = this.state.Task.filter((task) => {
            return task.name !== name;
        });
        this.setState({
            Task: tmpArr,
        });
    }

    isEditLevel(task) {
        if (task.name !== this.state.editName || this.state.isEdit === false) {
            // console.log("nor ", task.name);
            return <td className="text-center">{this.level(task.level)}</td>;
        } else if (this.state.isEdit === true) {
            // console.log("select ", task.name);
            return (
                <td className="text-center">
                    <Form.Control
                        value={this.state.newLevel}
                        as="select"
                        name="ds"
                        id="inputDs"
                        onChange={this.levelChange}
                    >
                        <option value={1}>Low</option>
                        <option value={2}>Medium</option>
                        <option value={3}>High</option>
                    </Form.Control>
                </td>
            );
        }
    }

    levelChange(e) {
        this.setState({
            newLevel: e.target.value,
        });
    }

    render() {
        return (
            // <tr key={index}>
            <tr>
                <td className="text-center">{this.state.index + 1}</td>
                {this.isEditName(this.state.task)}
                {this.isEditLevel(this.state.task)}
                {/* <td>{task.name}</td> */}
                {/* <td className="text-center">{this.level(task.level)}</td> */}
                <td className="text-center">
                    <ButtonGroup>
                        {this.toggleEditOrSave(this.state.task)}
                        {/* <Button
                    variant="warning"
                    onClick={() =>
                        this.handleEdit(task.name, task.level)
                    }
                >
                    Edit
                </Button> */}
                        <Button
                            variant="danger"
                            onClick={() => {
                                return this.delete(this.state.task.name);
                            }}
                        >
                            Delete
                        </Button>
                    </ButtonGroup>
                </td>
            </tr>
        );
    }
}
