import * as TYPE from "../Const/Types";
import TaskList from "../Tasks/Task.json";
var local;
local = localStorage.getItem("Tasks");
if (local === "undefined") {
    console.log("resetdata");
    //local = JSON.stringify(TaskList.Task);
    localStorage.setItem("Tasks", JSON.stringify(TaskList.Task));
}
local = localStorage.getItem("Tasks");
const initialState = JSON.parse(local);

export default (state = initialState, action) => {
    switch (action.type) {
        case TYPE.LIST:
            console.log("reducer", action);
            return state;
        case TYPE.ADD_TASK:
            console.log("reducer", action);
            state.push(action.task);
            // console.log("state", state);
            // console.log("js", JSON.stringify(state));
            localStorage.setItem("Tasks", JSON.stringify(state));
            return [...state];
        case TYPE.DELETE_TASK:
            console.log("reducer", action);
            const tmpArr = state.filter((task) => {
                return task.name !== action.taskName;
            });
            console.log("state", tmpArr);
            console.log("js", JSON.stringify(tmpArr));
            // localStorage.setItem("Tasks", JSON.stringify(tmpArr));
            return [...tmpArr];
        default:
            console.log("reducer", action);
            return state;
    }
    return state;
};
