import React, { useState } from "react";
import ButtonGroup from "react-bootstrap/ButtonGroup";
import InputGroup from "react-bootstrap/InputGroup";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Badge from "react-bootstrap/Badge";
import { connect } from "react-redux";
import * as Action from "../Actions/index";
import EditFields from "./EditFields";

import { Field, reduxForm, submit, SubmissionError } from "redux-form";

// import { Field, reduxForm } from "redux-form";
// import { submit } from "redux-form";
// import React, { useState, useEffect } from "react";
// import Edit from "./Edit";
function TaskRow(props) {
    // var createReduxForm = reduxForm({ form: "task" + props.task.id });
    // TaskRow = createReduxForm(TaskRow);
    // constructor(props) {
    //     super(props);
    //     this.nameChange = this.nameChange.bind(this);
    //     this.levelChange = this.levelChange.bind(this);
    // }
    const { handleSubmit, pristine, reset, submitting } = props;
    const [nameChange, setNameChange] = useState("");
    const [levelChange, setLevelChange] = useState(1);
    const [editName, setEditName] = useState(props.editName);
    const [isEdit, setIsEdit] = useState(false);
    // const [id, setId] = useState(props.task.id);

    function update(newName, newLevel) {
        console.log(newName, newLevel);
        // const index = this.state.Task.findIndex((task) => task.name == oldName);
        props.updateTask({ id: props.task.id, name: newName, level: newLevel });
    }

    function handleSave() {
        // props.toggleEdit();
        setIsEdit(false);
        console.log(editName, nameChange, parseInt(levelChange));
        update(nameChange, parseInt(levelChange));
        props.updateChart();
    }

    function toggleEditOrSave(task) {
        if (isEdit) {
            return (
                <Button
                    variant="warning"
                    // onClick={() => handleSave(task.name, task.level)}
                    onClick={() => props.submitform()}
                >
                    Done
                </Button>
            );
        } else {
            return (
                <Button
                    variant="warning"
                    onClick={() => handleEdit(task.name, task.level)}
                >
                    Edit
                </Button>
            );
        }
    }

    function handleEdit(name, level) {
        console.log(name);
        console.log(nameChange);
        props.editTask({
            id: props.task.id,
            name: props.task.name,
            level: props.task.level,
        });
        setIsEdit(true);
        setNameChange(name);
        setLevelChange(level);
        setEditName(name);
        console.log(nameChange);
        // props.toggleEdit();
        // props.newTask({ name: name, level: level });
        // props.editTask({ name: name, level: level });
    }

    function level(level) {
        switch (level) {
            case 1:
                return <Badge variant="light">Low</Badge>;
            case 2:
                return <Badge variant="info">Medium</Badge>;
            case 3:
                return <Badge variant="danger">High</Badge>;
            default:
                return <Badge variant="primary">No Level</Badge>;
        }
    }

    function test(value) {
        console.log("test ", value);
    }

    function isEditName(task) {
        // console.log(task.name !== editName || isEdit === false);

        if (task.name !== editName || isEdit === false) {
            // console.log(
            //     "nor ",
            //     task.name,
            //     this.props.editName,
            //     this.props.isEdit
            // );

            return <td>{task.name}</td>;
        } else if (isEdit === true) {
            // console.log(
            //     "tex ",
            //     task.name,
            //     this.props.editName,
            //     this.props.isEdit
            // );

            return (
                // <Edit newName={this.state.newName}></Edit>

                <td colSpan={2}>
                    {/* <Row>
                        <Col md={9}>
                            <EditFields
                                nameChange={task.name}
                                onSubmit={test}
                            ></EditFields>
                        </Col>
                        <Col md={3}>
                            <Form.Control
                                value={levelChange}
                                as="select"
                                name="ds"
                                id="inputDs"
                                onChange={(e) => setLevelChange(e.target.value)}
                            >
                                <option value={1}>Low</option>
                                <option value={2}>Medium</option>
                                <option value={3}>High</option>
                            </Form.Control>
                        </Col>
                    </Row> */}
                    <InputGroup>
                        <EditFields
                            nameChange={task.name}
                            onSubmit={test}
                        ></EditFields>
                        <Form.Control
                            value={levelChange}
                            as="select"
                            name="ds"
                            id="inputDs"
                            onChange={(e) => setLevelChange(e.target.value)}
                        >
                            <option value={1}>Low</option>
                            <option value={2}>Medium</option>
                            <option value={3}>High</option>
                        </Form.Control>
                    </InputGroup>

                    {/* <Form.Control
                        type="text"
                        value={nameChange}
                        onChange={(e) => setNameChange(e.target.value)}
                    ></Form.Control> */}

                    {/* <form
                        onSubmit={() => {
                            test();
                        }}
                    >
                        <Field
                            name="name"
                            type="text"
                            component={renderField}
                            // label="last name"
                        />
                    </form> */}
                    {/* <Field
                        // previousValue={nameChange}
                        name="row"
                        value={nameChange}
                        className="form-control"
                        component="input"
                        type="text"
                    /> */}
                </td>
            );
        }
    }

    function isEditLevel(task) {
        if (task.name !== editName || isEdit === false) {
            return <td className="text-center">{level(task.level)}</td>;
        } else if (isEdit === true) {
            // console.log("select ", task.name);
            return null;
            // <td className="text-center">
            //     <Form.Control
            //         value={levelChange}
            //         as="select"
            //         name="ds"
            //         id="inputDs"
            //         onChange={(e) => setLevelChange(e.target.value)}
            //     >
            //         <option value={1}>Low</option>
            //         <option value={2}>Medium</option>
            //         <option value={3}>High</option>
            //     </Form.Control>
            // </td>
        }
    }

    // function levelChange(e) {
    //     // this.setState({
    //     //     newLevel: e.target.value,
    //     // });
    //     console.log(e.target.value);
    //     this.props.newTask({
    //         name: this.props.newTaskName,
    //         level: e.target.value,
    //     });npm
    //
    const renderField = ({ input, label, type, meta: { touched, error } }) => {
        // console.log(input);
        return (
            <div>
                <h6>
                    <Badge pill variant="primary">
                        {"OldName: " + nameChange}
                    </Badge>
                </h6>
                <div>
                    <input
                        {...input}
                        type={type}
                        className="form-control"
                        // value={input.value}
                    />
                </div>
                {/* {touched && error && <span className="text-danger">{error}</span>} */}
            </div>
        );
    };

    console.log("row-render");
    // console.log(handleSubmit);
    // console.log(
    //     "this.props.editTaskTarget",
    //     this.props.editTaskTarget ? this.props.editTaskTarget : null
    // );
    return (
        // <tr key={index}>
        <tr>
            <td className="text-center">{props.index + 1}</td>
            {isEditName(props.task)}
            {isEditLevel(props.task)}
            {/* <td>{task.name}</td> */}
            {/* <td className="text-center">{this.level(task.level)}</td> */}
            <td className="text-center">
                <ButtonGroup>
                    {toggleEditOrSave(props.task)}
                    {/* <Button
                    variant="warning"
                    onClick={() =>
                        this.handleEdit(task.name, task.level)
                    }
                >
                    Edit
                </Button> */}
                    <Button
                        variant="danger"
                        onClick={() => {
                            // return this.delete(this.state.task.name);
                            return props.deleteTask(props.task.id);
                        }}
                    >
                        Delete
                    </Button>
                </ButtonGroup>
            </td>
        </tr>
    );
}

const mapStateToProps = (state) => ({
    // isEdit: state.ToggleEdit,
    editName: state.EditTask.name,
    // editTaskTarget: state.EditTask,
    newTaskName: state.NewTask.name,
    newTaskLevel: state.NewTask.level,
});

function mapDispatchToProps(dispatch) {
    return {
        deleteTask: (taskId) => {
            dispatch(Action.deleteTask(taskId));
        },
        toggleEdit: () => {
            dispatch(Action.toggleEdit());
        },
        editTask: (task) => {
            dispatch(Action.editTask(task));
        },
        newTask: (task) => {
            dispatch(Action.newTask(task));
        },
        updateTask: (newTask) => {
            dispatch(Action.updateTask(newTask));
        },
        updateChart: () => {
            dispatch(Action.resetChart());
        },
        submitform: () => {
            dispatch(submit("row"));
        },
    };
}

const sleep = (ms) => new Promise((resolve) => setTimeout(resolve, ms));

function submitform(values) {
    console.log(values);
    // return sleep(1000) // simulate server latency
    //     .then(() => {
    //         console.log("vô", values);
    //         console.log("vô", );
    //     });
}
TaskRow = reduxForm({
    // a unique name for the form
    form: "row",
    onSubmit: submitform,
})(TaskRow);
connect(mapStateToProps, mapDispatchToProps)(submitform);
export default connect(mapStateToProps, mapDispatchToProps)(TaskRow);

//export default connect(mapStateToProps, mapDispatchToProps)(TaskRow);
