import * as TYPE from "../Const/Types";
import connection from "./ConnectHub";
const initialState = [];

export default (state = initialState, Action) => {
    switch (Action.type) {
        case TYPE.UPDATE_CHAT:
            state.push({ username: Action.username, message: Action.message });
            return [...state];

        default:
            return state;
    }
};
