import React from "react";
import { Field, reduxForm, submit, SubmissionError } from "redux-form";
import * as Action from "../Actions/index";
import Badge from "react-bootstrap/Badge";
import Form from "react-bootstrap/Form";
import ButtonGroup from "react-bootstrap/ButtonGroup";
import Button from "react-bootstrap/Button";
import { connect } from "react-redux";

function EditForm(props) {
    const { handleSubmit, pristine, reset, submitting } = props;

    const renderSelect = ({ input, meta, type, placeholder }) => {
        return (
            <Form.Control as="select" custom {...input}>
                <option value={1}>Low</option>
                <option value={2}>Medium</option>
                <option value={3}>High</option>
            </Form.Control>
        );
    };
    function level(level) {
        switch (level) {
            case 1:
                return <Badge variant="light">Low</Badge>;
            case 2:
                return <Badge variant="info">Medium</Badge>;
            case 3:
                return <Badge variant="danger">High</Badge>;
            default:
                return <Badge variant="primary">No Level</Badge>;
        }
    }
    return (
        <tr>
            <td className="text-center">{props.index + 1}</td>
            <td colSpan={3} className="td">
                <div>
                    <Form onSubmit={handleSubmit} className="container-form">
                        {/* <div className="container"> */}
                        <div className="name">
                            <h6>Old Name: {props.task.name}</h6>
                            <div>
                                <Field
                                    className="form-control"
                                    name="name"
                                    // component={renderText}
                                    component="input"
                                    // type="text"
                                    placeholder="First Name"
                                />
                            </div>
                        </div>

                        <div className="level">
                            <h6>Old Level: {level(props.task.level)}</h6>

                            <div>
                                <Field
                                    name="level"
                                    // component="select"
                                    component={renderSelect}
                                ></Field>
                            </div>
                        </div>

                        <div className="action">
                            <div className="button-form text-center  ">
                                <ButtonGroup
                                    aria-label="Basic example"
                                    style={{
                                        width: "100%",
                                        height: "100%",
                                    }}
                                >
                                    <Button variant="warning" type="submit">
                                        Done
                                    </Button>
                                    <Button variant="danger">Cancel</Button>
                                    {/* <Button variant="secondary">Right</Button> */}
                                </ButtonGroup>
                            </div>
                        </div>
                        {/* </div> */}
                    </Form>
                </div>
            </td>
        </tr>
    );
}
const mapStateToProps = (state) => ({});

const mapDispatchToProps = (dispatch) => {
    return {
        toggleEdit: () => {
            dispatch(Action.toggleEdit());
        },
    };
};

EditForm = reduxForm({
    // a unique name for the form
    form: "row",
})(EditForm);

export default connect(mapStateToProps, mapDispatchToProps)(EditForm);
